export const asyncLocalStorage: {
  isEnabled: boolean
  storage: null | AsyncLocalStorage<HttpContext>
  create(): AsyncLocalStorage<HttpContext>
  destroy(): void
} = {
  /**
   * Check if the async local storage for the HTTP
   * context is enabled or not
